mapper.select:
select
TERMINAL_IP, TERMINAL_ID, TERMINAL_NAME, TERMINAL_PORT, TERMINAL_PASS, STATUS,USER_ID,TERMINAL_TITLE
from PUBLIC.TB_TERMINAL


        and TERMINAL_ID = #{terminalId}


        and TERMINAL_IP, = #{terminalId}


        and TERMINAL_ID = #{userId}


        and TERMINAL_TITLE = #{terminalTitle}


        and TERMINAL_NAME = #{terminalName}


        and TERMINAL_PORT = #{terminalPort}


        and TERMINAL_PASS = #{terminalPass}


        and STATUS = #{status}
